function deepCompare(o1, o2) {
    if (typeof o1 == typeof o2) {
        if (typeof o1 == 'object') {
            for (var key in o1) {
                if (!deepCompare(o1[key], o2[key]))
                    return false;
            }
            for (var key in o2) {
                if (!o1.hasOwnProperty(key))
                    return false;
            }
            return true;
        }
        else if (typeof o1 == 'array') {
            if (o1.length == o2.length) {
                for (var i = 0; i < o1.length; ++i) {
                    return deepCompare(o1[i], o2[i]);
                }
            }
        }
        else
            return o1 == o2;
    }
    return false;
}

