define('nd.purchase',["nd.analytics","nd.ui.dialog.purchase", "nd.ui.dialog.archivepurchase", "nd.purchase.provider", "nd.navigation"], function (ndanalytics,dialog, dialogarchive, provider) {

    var self = {};
    var dfd;
    
    $(dialog).bind("submit", function (sender, args) {

        provider.checkout().done(
            function (data) {
                dfd.resolve();
                dialog.hide();
                
                $.nd.event.trigger("stateChanged",self);
                ndanalytics.pageView("/reg/product-purchased");

                if (!data || !data.Transaction) return;

                var transaction = {
                    id: data.Transaction.Id,
                    revenue: data.Transaction.Revenue,
                    currency: data.Transaction.Currency,
                };

                var items = [];
                
                for (var i = 0; i < data.Transaction.Items.length; i++) {
                    var item = data.Transaction.Items[i];
                    items.push({
                        id: item.Id,
                        name: item.Name,
                        price: item.Price
                    });
                }

                ndanalytics.transaction(transaction, items);
            }
        ).fail(
            function () {

                dfd.reject();
            });

    });
    
    $(dialog).bind("selectProduct", function (sender, args) {

        provider.addProductToBasket(args.selectedProduct);
        
    });
    
    $(dialog).bind("changeBillingInfo", function (sender, args) {

        var params = "";
        
        if (args) {

            if (args.requestedContent) {
                if (args.requestedContent.issueId) {
                    params += "&RequestedContent.issueId=" + args.requestedContent.issueId;
                }

                if (args.requestedContent.articelId) {
                    params += "&RequestedContent.articleId=" + args.requestedContent.articelId;
                }
            }

            if (args.selectedProduct) {
                params += "&preselectedproduct.id=" + args.selectedProduct.Id;
                params += "&preselectedproduct.type=" + args.selectedProduct.Type;
            }
        }

        $.nd.navigation.redirect(window.basePath + "Accounting/Payment/?GoToStep=2" + params);
    });
    
    $(dialog).bind("hide", function () {

        dfd.reject();
        
    });

    self.purchase = function (requestedContent) {
        
        dfd = $.Deferred();

        provider.getPurchaseInfo(requestedContent).done(function (purchaseInfo) {

            dialog.show({ requestedContent:requestedContent, purchaseInfo: purchaseInfo });

        }).fail(dfd.reject);

        return dfd.promise();
    };

    self.purchasearchive = function (requestedContent) {
        
        dfd = $.Deferred();

        provider.getArchivePurchaseInfo(requestedContent).done(function (purchaseInfo) {

            dialog.show({ requestedContent:requestedContent, purchaseInfo: purchaseInfo });

        }).fail(dfd.reject);

        return dfd.promise();
    };

    return self;
    
});
