					self.Issue(data.Issue);
					self.PageNumber(data.PageNumber);
					self.NewspaperName(data.ContentName || "");
					self.IssueDate(data.IssueDate || "");
					self.Section(data.SectionName || "");
					self.Title(data.Title || "");
					self.SubTitle(data.Text || "");
					self.Uid(data.Uid);
					self.Culture(data.Culture);
					self.Source(getSource(data.Uid, data.Culture));
					self.Picture(getPicture(data.Image, data.Issue));
					self.ImageUrl(getImageUrl(data.Issue, data.PageNumber));
					
					var _vote = $.nd.social.votes.get(data.ArticleId);
					self.Support(_vote.support());
					self.Oppose(_vote.oppose());
