					Title: ko.observable(""),
					ImageUrl: ko.observable(""),
					Uid: ko.observable(""),
					Culture: ko.observable(""),
					RemainedTime: ko.observable(0),
					PlayedTime: ko.observable(0),
					Duration: ko.observable(""),
					Support: ko.observable(0),
					Oppose: ko.observable(0),
					Vote: ko.observable(0),
