		function setClipPosition(data) {
			if (data) {
				var thumbnailHeight = Math.floor(parseInt(view.find("#thumbnail").css("height"), 10));     //
				var thumbnailWidth = Math.floor(parseInt(view.find("#thumbnail").css("width"), 10));
				var width = Math.floor(data.W * thumbnailWidth);
				var top = Math.floor(data.Y * thumbnailHeight);                                            //
				//var thumbnailLeft = view.find('#ClipThumbnail').css('left');
				//var thumbnailLeft2 = (viewPanel.width() - thumbnailWidth) / 2;
				var left = Math.floor(data.X * thumbnailWidth);
				var right = Math.floor(left + width);
				var height = thumbnailHeight * data.H;                                                     //
				var bottom = Math.floor(top + height);                                                     //
				var rect = "rect(" + top + "px, " + right + "px, " + bottom + "px, " + left + "px)";

				view.find("#r-lightBox-fd").css("clip", rect);
			}
		}

