define('nd.data.gallerypuzzleitem',["nd.core"], function () {

    function _PuzzleItem(obj, postId) {
        this._artifactId = obj.ArtifactId;
        this._height     = obj.PreviewMeta.Height;
        this._width      = obj.PreviewMeta.Width;
        this._postId     = postId;
        this._meta       = null;
        this._type       = _PuzzleItem.types.image;
        this._previewUrl = obj.PreviewUrl;

        if (obj.VideoMeta) {
            this._type = _PuzzleItem.types.video;
            this._meta = obj.VideoMeta;
        }

        this._left = 0;
        this._top  = 0;
    }

    _PuzzleItem.types = {video: "video", image: "image"};
    _PuzzleItem.videoTypes = {youtube: 1, vimeo: 2};

    _PuzzleItem.prototype.postId = function() {
        return this._postId;
    };
    _PuzzleItem.prototype.artifactId = function() {
        return this._artifactId;
    };
    _PuzzleItem.prototype.meta = function() {
        return this._meta;
    };
    var urlRegex = /w(\d+)-h(\d+)/;
    _PuzzleItem.prototype.previewUrl = function(width, height, accountNumber) {
        width  = width  || this.width();
        height = height || this.height();

        var url = this._previewUrl;
        if (this._previewUrl.indexOf("http") != 0) {
            if (!accountNumber) {
                console.log("WARNING!!! accountNumber can not be null.");
            }
            url = $.nd.services.baseUrl() + (this._previewUrl.replace('{userId}', accountNumber));
        }

        return url.replace(urlRegex, "w{0}-h{1}".replace('{0}', width).replace('{1}', height));
    };
    _PuzzleItem.prototype.type = function () {
        return this._type;
    };
    _PuzzleItem.prototype.autoPlay = function () {
        if (this._type == _PuzzleItem.types.video) {
            if (arguments.length) {
                this._autoPlay = arguments[0];
            }
            return !!this._autoPlay;
        }
        return false;
    };
    _PuzzleItem.prototype.scaleToWidth = function (width) {
        if (width){
            var scale    = width / this._width;
            this._height = Math.floor(this._height * scale);
            this._width  = width;
        }
    };
    _PuzzleItem.prototype.scaleToHeight = function (height) {
        if (height){
            var scale    = height / this._height;
            this._width  = Math.floor(this._width * scale);
            this._height = height;
        }
    };
    _PuzzleItem.prototype.height = function () {
        return this._height;
    };
    _PuzzleItem.prototype.width = function () {
        return this._width;
    };
    _PuzzleItem.prototype.left = function (left) {
        if (arguments.length){
            this._left = left;
        }
        return this._left;
    };
    _PuzzleItem.prototype.top = function (top) {
        if (arguments.length){
            this._top = top;
        }
        return this._top;
    };

    return $.nd.data.GalleryPazzleItem = _PuzzleItem;
});
