		if (previewInfo.Article) {
			previewInfo.Article.ThumbnailUrl = (previewInfo.Article.ArticleThumbnailUrl) ? previewInfo.Article.ArticleThumbnailUrl : previewInfo.Article.PageThumbnailUrl;
		}
		function getContentType(info) {
			if (info.Article) {
				return "article";
			} else if (info.Page) {
				return "page";
			} else if (info.Issue) {
				return "issue";
			}
		}

		function submit(submissionType) {
			var to = model.to();
			if (to) to = to.split(regexDelimiters);

			var args = { contentType: model.selectedContentType(),
				from: model.from(),
				to: to,
				subject: model.subject(),
				userComments: model.userComments() },
				eventType = submissionType;

			if (args.contentType == "page" || args.contentType == "issue") {
				args.page = previewInfo.Page.PageNumber;
				args.issue = previewInfo.Issue.Issue;
			}


			$(self).trigger(eventType, args);
		}

		model =
		{
			selectedContentType: ko.observable(getContentType(previewInfo)),
			previewInfo: ko.observable(previewInfo),
			from: ko.observable(""),
			to: ko.observable(shareEmail).extend({
				emailList: {
					delimiters: emailDelimiters
				},
				required: true,
				trackDirtyState: true
			}),
			userComments: ko.observable(""),

			submit: function () {
				if (!ko.nd.validate(this)) return;

				submit("submit");

				$.nd.userSettings.global.set(shareEmailSettingsKey, this.to());
			},
			close: function () {
				dialog.hide();
			},
			resize: function () {
				dialog.resize();
			},
			shareUsingPersonalEmail: function () {

				if (!ko.nd.validate(this)) return;

				submit("submitViaUserMail");
			}
		};

		model.subject = ko.computed(function () {
			var info = model.previewInfo();
			if(model.selectedContentType() == "article") {
				return info.Article.Title + ' (' + info.Article.NewspaperName + '), ' + info.Article.IssueDate + ', ' + info.Page.PageNumber;
			} else if(model.selectedContentType() == "page") {
				return info.Page.NewspaperName + ", " + info.Page.IssueDate + ', ' + info.Page.PageNumber;
			}

			return info.Issue.NewspaperName + ", " + info.Issue.IssueDate;
		});
