define('nd.ui.dialog.addopinion',[
    "nd.opinions.manager"
    ,"modernizr"
    ,"nd.ui.dialog"
    ,"nd.keycode"
    ,"nd.enums"
], function (opinionsManager) {

    window.pdmodules.push("nd.ui.dialog.addopinion");

    var self = $.nd.ui.dialog.addOpinion = {},
        template = "v7.Client.Dialogs.AddOpinion",
        filters = $.nd.opinionFilter;
    $.nd.ui.dialog.addOpinion.modes = {find:1, findOrCreate:2};

    function getModel(dialog, config) {
        var model = {
            opinion: ko.observable(null),
            opinionText: ko.observable(config.opinionText || ""),
            showBack: ko.observable(config.mode == $.nd.ui.dialog.addOpinion.modes.find),
            showClose: ko.observable(config.mode == $.nd.ui.dialog.addOpinion.modes.findOrCreate),
            myOpinions: ko.observableArray(),
            relatedOpinions: ko.observableArray(),
            mainPanelVisible: ko.observable(false),
            loadedItems: ko.observable(0),
            canSupport: ko.observable(!!config.canSupport),
            loadingDeferred: $.Deferred(),
            excludeMyOpinions: !!config.excludeMyOpinions,
            filters: filters,
            opinionContext: config.opinionContext,
            currentFilter: ko.observable(filters.growing),
            _findSuggestedOpinions: function (search, timeout, skip) {

                if(model._findOpinionsTimerId) {
                    clearTimeout(model._findOpinionsTimerId);
                }
                var dfd = $.Deferred();
                model._findOpinionsTimerId = setTimeout(function () {
                    opinionsManager.findSuggestedOpinions(search, skip, {excludeMyOpinions:model.excludeMyOpinions, orderby:model.currentFilter()}).done(function (data) {
                        dfd.resolve(data);
                        model._findOpinionsTimerId = null;
                    });
                }, (typeof timeout == 'undefined') ? 1000 : timeout);

                return dfd.promise();
            },
            _mergeLists: function(obsArray, newArray) {
                if(obsArray().length == 0) {
                    obsArray(newArray);
                }
                else {
                    for(var i = 0; i < newArray.length; i++) {
                        obsArray.push(newArray[i]);
                    }
                }
            },
            _loadOpinions: function(searchValue, skip) {
                if(model._loadOpinionsTimeoutId) {
                    clearTimeout(model._loadOpinionsTimeoutId);
                }
                if(!skip) {
                    model.loadedItems(0);
                }
                model._loadOpinionsTimeoutId = setTimeout(function() {
                    model.loadingDeferred = model._findSuggestedOpinions(searchValue, 0, skip).done(function (data) {
                        var myOpinions = data && data.myOpinions || [];
                        var relatedOpinions = data && data.opinions || [];

                        if(!skip) {
                            model.myOpinions([]);
                            model.relatedOpinions([]);
                        }

                        model._mergeLists(model.myOpinions, myOpinions);
                        model._mergeLists(model.relatedOpinions, relatedOpinions);

                        if(!model.mainPanelVisible()) {
                            /* first time load */
                            model.mainPanelVisible(true);
                            dialog.resize();
                        }
                        model.loadedItems(data.loadedItems)
                    });
                    model._loadOpinionsTimeoutId = null;
                }, 200);
            },
            _setOpinion: function(opinion) {
                model.opinion(opinion || null);
            },
            _goBack: function(state, newOpinion) {
                if(state == $.nd.ui.dialogStatus.ok) {
                    dialog.hide(state, {opinion: model.opinion(), opinionText: model.opinionText(), newOpinion:!!newOpinion});
                } else {
                    dialog.hide(state);
                }
            },
            _reset: function() {
                model.opinionText("");
                model.opinion(null);
                model._loadOpinions("");
            },
            _isLastOpinionVisible: function(scrollPanelHeight) {
                var scrollMarkerTop = dialog.htmlRoot().find(".load-more-marker").offset().top;
                var delta = 150;
                return scrollMarkerTop - scrollPanelHeight < delta;
            },
            _trimOpinionText: function(text) {
                return (text||"").trim().replace(/\s+/gi, ' ');
            },
            _findOpinion: function(opinionText) {
                var df = $.Deferred();
                model.loadingDeferred.done(function() {
                    var opinion = null;
                    var relatedOpinions = model.relatedOpinions();
                    opinionText = model._trimOpinionText(opinionText);
                    for(var i = 0; i < relatedOpinions.length; i++) {
                        var o = relatedOpinions[i];
                        if(o.opinion && opinionText.match(new RegExp(model._trimOpinionText(o.opinion), "i"))) {
                            opinion = o;
                            break;
                        }
                    }
                    df.resolve(opinion);
                });
                return df.promise();
            },
            scroll: function(_, e) {
                var scrollPanelHeight = $(e.target).outerHeight();

                if(model._isLastOpinionVisible(scrollPanelHeight)) {
                    model._loadOpinions(model.opinionText(), model.loadedItems() + 1);
                }
            },
            suggestedOpinionClick: function(opinion) {
                model._setOpinion(opinion);
                model._goBack($.nd.ui.dialogStatus.ok);
            },
            opinionInputChange:function(_, event) {
                model._loadOpinions(event.target.value);
            },
            opinionInputKeyUp: function(_, event) {
                if (event.keyCode === $.nd.keycode.enter) {
                    if(event.target.value) {
                        var opinionText = event.target.value;
                        model.opinionText(opinionText);
                        model.create();
                    }
                }
            },
            create: function() {
                if(!model.closing) {
                    model.closing = true;

                    model._findOpinion(model.opinionText()).done(function(opinion) {
                        if(opinion) {
                            model._setOpinion(opinion);
                            model._goBack($.nd.ui.dialogStatus.ok);
                        } else if(!opinion && model.opinionText()) {
                            opinionsManager.createOpinion(model.opinionText(), model.opinionContext).done(function(o) {
                                model._setOpinion(o);

                                var newOpinion = true;
                                model._goBack($.nd.ui.dialogStatus.ok, newOpinion);
                            }).fail(function() {
                                model.closing = false;
                            });
                        }
                    });
                }
            },
            resetClick: function() {
                model._reset();
            },
            closeClick: function() {
                model._goBack($.nd.ui.dialogStatus.cancel);
            },
            backClick: function() {
                model._goBack($.nd.ui.dialogStatus.cancel);
            },
            filter: function(byFilter) {
                return function() {
                    if(model.currentFilter() != byFilter) {
                        if(model.applyingFilterProcessId) {
                            clearTimeout(model.applyingFilterProcessId);
                        }

                        model.applyingFilterProcessId = setTimeout(function() {
                            model.currentFilter(byFilter);
                            model._loadOpinions(model.opinionText());
                            model.applyingFilterProcessId = null;
                        }, 50);
                    }
                }
            },
            toggleSupport: function(opinion) {
                opinion[opinion.supporting() ? "unfollow" : "follow"]();
            }
        };

        model._loadOpinions(model.opinionText());
        return $.Deferred().resolve(model);
    }

    self.show = function(parentDialog, config) {
        var dialog = $.nd.ui.dialog.createInstance(parentDialog);

        config = config||{};
        config.mode = config.mode || $.nd.ui.dialog.addOpinion.modes.find;
        getModel(dialog, config).done(function(model) {
            dialog.onHide(function() {});

            dialog.onResize(function(e, data) {
                var delta = 70;
                var searchHeight = dialog.htmlRoot().find(".pop-segment-visible").outerHeight();
                var height = data.screenHeight - data.headerHeight - searchHeight - delta;
                dialog.htmlRoot().find(".pop-segment-scrollable").css("height", height + "px");
            });

            dialog.show({
                templateName: template,
                model: model,
                name: 'AddOpinion',
                focusElement: 'input',
                freezeTop:true
            });
            dialog.resize();
        });

        return dialog;
    };

    return self;
});

