        var scrollToPost = function(model, postId) {
            if (postId) {
                var posts = model.comments(),
                    index = _.findIndex(posts||[], function(post) {
                        return post.id == postId || post.postKey == postId;
                    });

                if (index > 1) { /* do not scroll if comment is the first */
                    var $comment = model.controls().panel.comments.find("[data-name='Pop-Comment']:eq(" + index + ")");
                    if ($comment.size()) {
                        var commentTop    = getCommentTopPosition($comment, model);
                        var frameHeight   = getFrameHeight(model);

                        var topShift = (commentTop + frameHeight) - frameHeight;
                        if (topShift > -1) {
                            model.controls().panel.comments.animate({ scrollTop: topShift }, {
                                duration: 800,
                                queue: false,
                                step: $.noop //disables nd.animate
                            });
                        }
                    }
                }
            }
        };

