		var postEdit = function () {
			if(model.posting()) return;

			var viewState = self._viewState.get();
			if(!viewState || !viewState.comment) {
				return;
			}

			model.posting(true);
			var comment = viewState.comment;
            var data = {
                issueId: model.issueId,
                articleId: model.articleId,
                parentPostId: viewState.comment.id,
                text: $Comment.toHtmlText(model.text())
                //postId: viewState.comment.id,
                //newText:toHtmlText(model.text())
            };
            if(model.opinionText()) {
                data.opinionText = model.opinionText();
                if(model.opinionId()) {
                    data.opinionId = model.opinionId();
                }
            } else {
                model.resetOpinion();
            }

            var attachments = getAttchd(model.uploadedFileList());
            if (attachments){
                $.extend( data, attachments );
            }

			$.nd.contentservice.post("articlecomments", "EditComment", data).done(function (result) {
				if(result && result.ok && result.post) {
                    model.listView();
                    if(result.comments) {
                        /* reload whole thread */
                        var comments = result.comments;
                        model.totalSupport(comments.LikeItVotes);
                        model.totalOppose(comments.HateItVotes);
                        model.totalCount(comments.PostsCount);
                        model.vote(comments.CurrentArticleVote);
                        model.comments(mapComments(comments));
                        model.popularOpinions(model._mapOpinions(comments.PopularOpinions));
                    } else {
                        var post = result.post;
                        comment.text($Comment.preProcess(post.Text));
                        comment.rawText($Comment.fromHtmlText(post.RawText));
                        comment.htmlText(post.RawText);
                        var oldPostId = comment.id;
                        comment.id = post.PostId;
                        var comments = model.comments();
                        for(var i = 0; i < comments.length; i++) {
                            var c = comments[i];
                            if(c.parentId == oldPostId) {
                                c.parentId = oldPostId;
                            }
                        }
                    }
                    dialog.resize();
                    model.enforceLoadingImages.notifySubscribers();
					model.posting(false);

				}
			}).fail(function() {
				model.posting(false);
			});
		};

		var updateTotalVote = function(articleId, newVote) {
			if(model.vote() != newVote) {
				var supp = model.totalSupport() + (newVote > 0 ? 1 : (model.vote() ? -1 : 0));
				var opp = model.totalOppose() + (newVote < 0 ? 1 : (model.vote() ? -1 : 0));
				model.totalSupport(supp);
				model.totalOppose(opp);
				model.vote(newVote);
				$.nd.social.votes.vote(articleId, newVote, null);
			}
		};

