		var updateCommentVote = function(comment, event, vote) {
			event.stopPropagation();

			if(comment.authorId != $.nd.userInfo.getAccountNumber()) {
				var updateFields = function(comment, likes, dislikes) {
					if(comment.totalSupport() != likes) {
						comment.totalSupport(likes);
					}
					if(comment.totalOppose() != dislikes) {
						comment.totalOppose(dislikes);
					}
				};

				if(comment.vote() != vote) {

					if(comment.authorId) {
						$.nd.contentservice.get("articlecomments", "VoteForPost", {
							issueId: model.issueId,
							postId: comment.id,
							vote: vote
						}).done(function (post) {
							updateFields(comment, post.LikeItVotes, post.HateItVotes);
						});
					}

					var likes = comment.totalSupport() + (vote > 0 ? 1 : 0) + (comment.vote() > 0 ? -1 : 0);
					var dislikes = comment.totalOppose() + (vote < 0 ? 1 : 0) + (comment.vote() < 0 ? -1 : 0);
					comment.vote(vote);
					updateFields(comment, likes, dislikes);

					comment.vote(vote);
				}

				/* the panel will be hidden */
				showCommentToolsPanel(comment, event);
			}

			return false;
		};

		var updatePositionProperties = function() {
			model.scrollerHeight("auto");

            var windowHeight = $.windowHeight() + $(window).scrollTop();
			var margin = windowHeight * 0.1;
			var maxHeight = windowHeight - margin*2;
			var dialogHeight = model.controls().root.outerHeight() + 20;
            var toolbarHeight = model.controls().root.find("header.toolbar:visible").outerHeight();

			var scrollerHeight = 0;
			if(dialogHeight < maxHeight) {
				scrollerHeight = dialogHeight - toolbarHeight;
			} else {
				scrollerHeight = maxHeight - toolbarHeight;
			}

			model.scrollerHeight(scrollerHeight + 'px');
            model.enforceLoadingImages.notifySubscribers();
		};

