    // priority: 1. Video, 2. Best image
    var getCommentMainPicture = function(post){
            var mainImage, resultUrl;
            if (post.artifacts && (mainImage = post.artifacts[0])) {
                var i = post.artifacts.length;
                while (i--) {
                    var item = post.artifacts[i];
                    if (item.VideoMeta){
                        mainImage = item;
                        break;
                    }
                    else if (item.PreviewMeta.Height * item.PreviewMeta.Width > mainImage.PreviewMeta.Height * mainImage.PreviewMeta.Width)
                    {
                        mainImage = item;
                    }
                }
                resultUrl = mainImage.PreviewUrl.replace(/w(\d+)-h(\d+)/, "w{0}-h{1}".replace('{0}', mainImage.PreviewMeta.Width).replace('{1}', mainImage.PreviewMeta.Height));
            }
            return resultUrl;
        };

