define('nd.statemanager',["nd.sponsorshipmanager", "nd.statemanager.catalog", "nd.services.api", "nd.services.authorization"], function (sponsorshipManager, catalogManager, services) {

    var self = {};

    var ts = 0, lastUpdateTime = 0, MIN_ELAPSE_TIME = 5 * 60000, FIRST_DELAY = 10;
    
    $.nd.event.bind(sponsorshipManager, "sponsorshipStateChanged", function () {
		$.nd.services.authorization.updateToken();
    });
    
    //try to get state from response header
    $(document).ajaxComplete(function (event, xhr, settings) {

		if(settings.ignoreInitState) {
			return;
		}

        var header = xhr.getResponseHeader('ndstate');

        if (!header) return;
        
        var state = JSON.parse(header);

        initState(state);
    });

   	self.reset = function() {
   		ts = 0;
   		lastUpdateTime = 0;
   	}
    
    //in case if we did not get state updated during X minutes we should request it
    function checkState() {

        if ((lastUpdateTime + MIN_ELAPSE_TIME) < getCurrentTime()) {
            requestState().done(function (state) {
                initState(state);
            });
        }
    }
    
    $.visiblity.setInterval(checkState, MIN_ELAPSE_TIME, FIRST_DELAY);
    
    function initState(state) {

        if (!state || !state.Ts) return;

        if (state.Ts <= ts) return;

        ts = state.Ts;
        
        lastUpdateTime = getCurrentTime();

        sponsorshipManager.init(state);

        catalogManager.init(state);
    }

    function getCurrentTime() {
        return new Date().getTime();
    }

    function requestState() {
        return services.get("State", "GetState");
    }

    return self;

});

