define('platform/extensions',[], function () {

    extentionsInit = function (stage) {

        var platform = initializerBase.parameters().systemName;

        if (platform == 'iPhone OS') {
            iOSInit(stage);
        }
        else if (platform == 'Android') {
            AndroidInit(stage);
        }
        else if (platform == 'Win8') {
            Win8Init(stage);
        }

    };

    function iOSInit(stage) {

        if (stage == 1) {

            browserNavigate = function (url) {
                var iframe = document.createElement("IFRAME");
                iframe.setAttribute("src", url);
                document.documentElement.appendChild(iframe);
                iframe.parentNode.removeChild(iframe);
                iframe = null;
            };

            window.jslog = function (type, message) {
                //var str = message + ': ' + callStack();
                var str = type + ': ' + message;
                browserNavigate("js-log:#log-js#" + str);
            };
        }
        else {
            CustomizationApply = function () {
                //$('.toolbar-switch-in').css('width', '110px');
                //$('.sw-page').css('display','none');
                //$('.sw-text').css('display','none');
            };

            onDataLoadReady = function () {
                var sys = initializer.parameters();
                $.nd.environment.platform($.nd.environment.platforms.ios);
                if (sys.deviceType.substring(0, 5) == 'iPad1') {
                    pdadapters.Parameters.set('screensToBuild', 2)
                }
                else {
                    pdadapters.Parameters.set('screensToBuild', 4)
                }
            };

            platform.getThumbPath = function (issueId, page, width) {
                var pageNo = Util.formatInt(page, 4);
                return pdadapters.Parameters.get('ThumbsPath') + pageNo + '.png';
            };
        }

    }

    function AndroidInit() {
        CustomizationApply = function () {
            addCSSFile('styles/style-d-android.css');
            var osVer = parseInt(initializerBase.parameters().osVer.substring(0, 3).replace(/\./g, ""));
            if (osVer == 40) {
                addCSSFile('styles/style-d-android-nofonts.css');
                if ($.nd.viewer.textView.fonts) {
                    $.nd.viewer.textView.fonts.list = function () {
                        var list = [
                            new $.nd.viewer.textView.fonts.Font("fontset1", "Default"),
                            new $.nd.viewer.textView.fonts.Font("fontset2", "Sans Serif"),
                        ];
                        return list;
                    }
                }
            } else if (osVer == 42 || osVer == 43 || (osVer == 41 && !((/samsung/gi).test(initializerBase.parameters().manufacturer)))) {
                $.nd.config.localextend({iScroll: {handleClick: false}, ui: {ActionTool: {eventName: "click"}}});
            }
            if (initializerBase.parameters().applycss) {
                addCSSFile(initializerBase.parameters().applycss);
            }
            $.browser.chrome = false;
            if (window.sponsorshipMgr) {
                window.sponsorshipMgr.init = function () {
                    return;
                };
            }
        };

        onDataLoadReady = function () {
            $.nd.environment.platform($.nd.environment.platforms.android);
            pdadapters.Parameters.set('screensToBuild', 2)
        };

        browserNavigate = function (url) {
            if (!desktopDebug)
                jwindow.call(url);
        };


        platform.getThumbPath = function (issueId, page, width) {
            var pageNo = Util.formatInt(page, 4);
            return pdadapters.Parameters.get('ThumbsPath') + '?issueId=' + issueId + "&page=" + page + '&width=' + width;
        };

    }

    function Win8Init() {

        CustomizationApply = function () {
            $.nd.viewer.textView.fonts.list = function () {
                var list = [
                    new $.nd.viewer.textView.fonts.Font("fontset1", "Segoe"),
                    new $.nd.viewer.textView.fonts.Font("fontset2", "Sans Serif"),
                ];
                return list;
            }

            TextViewAPI.contextMenuOverride('print', true);
            TextViewAPI.contextMenuOverride('share', true);
        }

        onDataLoadReady = function () {
            $.nd.environment.platform($.nd.environment.platforms.win8);
            pdadapters.Parameters.set('screensToBuild', 2);
        };
        browserNavigate = function (url) {
            window.external.notify(url);
        }

    }
});
